431\u0440\u0435\u043b\u0430 \u043d\u0430\u0434\u0435\u0436\u0434\u0443 \u043d\u0430 \u043f\u043e\u043a\u0443\u043f\u043a\u0443 \u043c\u043e\u0434\u043d\u043e\u0433\u043e \u043d\u0435\u0434\u043e\u0440\u043e\u0433\u043e \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0430, \u0445\u043e\u0442\u044f, \u0432 \u043c\u0430\u0433\u0430\u0437\u0438\u043d\u0435 \u0447\u0443\u0442\u044c \u0431\u044b\u043b\u043e \u043d\u0435 \u043a\u0443\u043f\u0438\u043b\u0430 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d ASUS \u0437\u0430 10 \u0442\u044b\u0441\u044f\u0447. \u0427\u0442\u043e \u043c\u043d\u0435 \u043f\u043e\u043d\u0440\u0430\u0432\u0438\u043b\u043e\u0441\u044c?! \u0410 \u0447\u0442\u043e \u043d\u0435 \u043e\u0447\u0435\u043d\u044c? \u041e\u0431\u043e \u0432\u0441\u0435\u043c \u043d\u0438\u0436\u0435, \u0430 \u044f \u0445\u043e\u0447\u0443 \u0440\u0430\u0441\u0441\u043a\u0430\u0437\u0430\u0442\u044c \u043e \u0434\u043e\u0441\u0442\u0430\u0432\u043a\u0435 \u0438 \u0441\u0430\u043c\u044b\u0445 \u043f\u0435\u0440\u0432\u044b\u0445 \u043f\u0440\u043e\u0431\u043b\u0435\u043c\u0430\u0445, \u043a\u043e\u0442\u043e\u0440\u044b\u0435 \u0432\u043e\u0437\u043d\u0438\u043a\u043b\u0438 \u0443 \u043c\u0435\u043d\u044f. \u041f\u0440\u043e\u0432\u0435\u0440\u0435\u043d\u043d\u043e\u0433\u043e \u043f\u0440\u043e\u0434\u0430\u0432\u0446\u0430 \u0441 \u0445\u043e\u0440\u043e\u0448\u0438\u043c \u0440\u0435\u0439\u0442\u0438\u043d\u0433\u043e\u043c \u0434\u043e\u043b\u0433\u043e \u0438\u0441\u043a\u0430\u0442\u044c \u043d\u0435 \u043f\u0440\u0438\u0448\u043b\u043e\u0441\u044c, \u043e\u043d \u043e\u043a\u0430\u0437\u0430\u043b\u0441\u044f \u043f\u043e\u0447\u0442\u0438 \u0441\u0430\u043c\u044b\u0439 \u043f\u0435\u0440\u0432\u044b\u0439, \u0431\u043e\u043b\u044c\u0448\u0435 1400 \u0437\u0430\u043a\u0430\u0437\u043e\u0432 \u0443\u0436\u0435 \u043d\u0430 \u0434\u0430\u043d\u043d\u044b\u0439 \u043c\u043e\u043c\u0435\u043d\u0442 \u0438 \u0441\u0440\u0435\u0434\u043d\u044f\u044f \u043e\u0446\u0435\u043d\u043a\u0430 4,9. \u0421\u0440\u0430\u0437\u0443 \u0436\u0435 \u0434\u0435\u043b\u044e\u0441\u044c , \u0430 \u0435\u0441\u043b\u0438 \u043e\u043d\u0430 \u0441\u0433\u043e\u0440\u0438\u0442, \u0442\u043e . \u0410 \u043f\u043e\u0441\u043c\u043e\u0442\u0440\u0435\u0432 \u043e\u0442\u0437\u044b\u0432\u044b \u0441 \u043f\u0440\u0438\u043b\u043e\u0436\u0435\u043d\u0438\u044f \u0410\u043b\u0438, \u043f\u043e\u043b\u043d\u043e\u0441\u0442\u044c\u044e \u0434\u043e\u0432\u0435\u0440\u0438\u043b\u0430\u0441\u044c \u043f\u0440\u043e\u0434\u0430\u0432\u0446\u0443 \u0438 \u0431\u0435\u0437 \u043f\u043e\u0441\u0442\u043e\u0440\u043e\u043d\u043d\u0438\u0445 \u043c\u044b\u0441\u043b\u0435\u0439 \u043e\u043f\u043b\u0430\u0442\u0438\u043b\u0430 \u0441\u0443\u043c\u043c\u0443, \u0443\u043a\u0430\u0437\u0430\u043d\u043d\u0443\u044e \u043d\u0438\u0436\u0435. \u041e\u043f\u043b\u0430\u0442\u0438\u043b\u0430 5300 \u0440\u0443\u0431(\u043f\u043e\u043f\u0430\u043b\u0430 \u043f\u043e\u0434 \u0443\u0434\u0430\u0447\u043d\u044b\u0439 \u043a\u0443\u0440\u0441 \u0434\u043e\u043b\u043b\u0430\u0440\u0430, \u0441\u0435\u0439\u0447\u0430\u0441 \u043d\u0435\u043c\u043d\u043e\u0433\u043e \u0434\u043e\u0440\u043e\u0436\u0435) \u0438 \u0437\u0430\u043a\u0430\u0437\u0430\u043b\u0430 \u0432\u0435\u0447\u0435\u0440\u043e\u043c, 22 \u0430\u0432\u0433\u0443\u0441\u0442\u0430, \u0430 2 \u0441\u0435\u043d\u0442\u044f\u0431\u0440\u044f \u043e\u043d \u0443\u0436\u0435 \u043e\u043a\u0430\u0437\u0430\u043b\u0441\u044f \u0443 \u043c\u0435\u043d\u044f \u0432 \u0440\u0443\u043a\u0430\u0445. \u0422\u0435\u043b\u0435\u0444\u043e\u043d \u043e\u0442\u0441\u043b\u0435\u0436\u0438\u0432\u0430\u043b\u0441\u044f \u043f\u043e\u0447\u0442\u0438 \u043a\u0430\u0436\u0434\u043e\u0434\u043d\u0435\u0432\u043d\u043e. \u0420\u043e\u0432\u043d\u043e \u0437\u0430 10 \u0434\u043d\u0435\u0439 \u043f\u0440\u0438\u0448\u0435\u043b \u0432 \u0422\u0443\u043b\u044c\u0441\u043a\u0443\u044e \u043e\u0431\u043b\u0430\u0441\u0442\u044c, \u0447\u0435\u043c\u0443 \u044f \u043e\u0447\u0435\u043d\u044c \u043e\u0431\u0440\u0430\u0434\u043e\u0432\u0430\u043b\u0430\u0441\u044c \u0438 \u043e\u0431\u044f\u0437\u0430\u0442\u0435\u043b\u044c\u043d\u043e \u043f\u043e\u0441\u0442\u0430\u0432\u043b\u044e 5 \u0437\u0432\u0435\u0437\u0434 \u0437\u0430 \u0434\u043e\u0441\u0442\u0430\u0432\u043a\u0443. \u0415\u0434\u0438\u043d\u0441\u0442\u0432\u0435\u043d\u043d\u043e\u0435, \u043e\u0447\u0435\u043d\u044c \u0441\u0442\u0440\u0430\u0448\u043d\u043e \u0431\u044b\u043b\u043e, \u0447\u0442\u043e \u043d\u0430 \u0430\u043f\u043f\u0430\u0440\u0430\u0442\u0435 \u0431\u0443\u0434\u0443\u0442 \u043f\u043e\u0432\u0440\u0435\u0436\u0434\u0435\u043d\u0438\u044f, \u0432\u0435\u0434\u044c \u043a\u0443\u0440\u044c\u0435\u0440\u044b \u043f\u0440\u0438 \u0434\u043e\u0441\u0442\u0430\u0432\u043a\u0435 \u043d\u0430 \u043f\u043e\u0447\u0442\u0443 \u043f\u043e\u0441\u044b\u043b\u043a\u0438 \u0431\u0443\u043a\u0432\u0430\u043b\u044c\u043d\u043e \u0417\u0410\u0428\u0412\u042b\u0420\u0418\u0412\u0410\u042e\u0422, \u0432\u0438\u0434\u0435\u043b\u0430 \u0441\u043e\u0431\u0441\u0442\u0432\u0435\u043d\u043d\u044b\u043c\u0438 \u0433\u043b\u0430\u0437\u0430\u043c\u0438, \u043a\u0430\u043a \u043a\u0438\u0434\u0430\u043b\u0438 \u043a\u043e\u0440\u043e\u0431\u043a\u0438 \u042d\u0439\u0432\u043e\u043d, \u043e\u0442 \u0447\u0435\u0433\u043e \u043f\u0440\u043e\u0434\u0443\u043a\u0446\u0438\u044f \u0432\u043d\u0443\u0442\u0440\u0438 \u043c\u043d\u0435\u0442\u0441\u044f \u0438 \u0434\u0430\u0436\u0435 \u0440\u0430\u0441\u043a\u0430\u043b\u044b\u0432\u0430\u0435\u0442\u0441\u044f. \u0410 \u043f\u043e\u0442\u043e\u043c \u0440\u0430\u0431\u043e\u0442\u043d\u0438\u043a\u0430\u043c \u043f\u043e\u0447\u0442\u044b \u0438 \u043f\u0440\u0435\u0434\u0441\u0442\u0430\u0432\u0438\u0442\u0435\u043b\u044f\u043c \u043f\u0440\u0438\u0445\u043e\u0434\u0438\u0442\u0441\u044f \u043a\u0440\u0430\u0441\u043d\u0435\u0442\u044c. \u041d\u043e, \u0443\u0436\u0435 \u0442\u043e\u0433\u0434\u0430 \u044f \u0443\u043f\u0443\u0441\u0442\u0438\u043b\u0430 \u043c\u043e\u043c\u0435\u043d\u0442 - \u043d\u0435 \u043f\u043e\u043f\u0440\u043e\u0441\u0438\u043b\u0430 \u0443 \u043f\u0440\u043e\u0434\u0430\u0432\u0446\u0430 \u043f\u043e\u043b\u043d\u043e\u0441\u0442\u044c\u044e \u0440\u0443\u0441\u0438\u0444\u0438\u0446\u0438\u0440\u043e\u0432\u0430\u043d\u043d\u0443\u044e \u043f\u0440\u043e\u0448\u0438\u0432\u043a\u0443 (\u0435\u043c\u0443 \u043e\u0431 \u044d\u0442\u043e \u043d\u0443\u0436\u043d\u043e \u043f\u0438\u0441\u0430\u0442\u044c \u043f\u0440\u0438 \u0437\u0430\u043a\u0430\u0437\u0435), \u0438 \u043f\u043e\u044d\u0442\u043e\u043c\u0443 \u043f\u043e\u043b\u0443\u0447\u0438\u043b\u0430 \u0442\u0435\u043b\u0435\u0444\u043e\u043d \u0441 \u043a\u0438\u0442\u0430\u0439\u0441\u043a\u043e\u0439 \u043f\u0440\u043e\u0448\u0438\u0432\u043a\u043e\u0439, \u043b\u043e\u043c\u0430\u043d\u043e - \u043f\u0435\u0440\u0435\u0432\u0435\u0434\u0435\u043d\u043d\u043e\u0439 \u043d\u0430 \u0440\u0443\u0441\u0441\u043a\u0438\u0439, \u0430 \u043d\u0435\u043a\u043e\u0442\u043e\u0440\u044b\u0435 \u044d\u043b\u0435\u043c\u0435\u043d\u0442\u044b \u0438 \u0432\u043e\u0432\u0441\u0435 \u043e\u0441\u0442\u0430\u043b\u0438\u0441\u044c \u043d\u0430 \u043a\u0438\u0442\u0430\u0439\u0441\u043a\u043e\u043c. (\u0441\u0435\u043d\u0441\u043e\u0440\u043d\u044b\u0435 \u043a\u043d\u043e\u043f\u043a\u0438 \"\u0432\u044b\u043a\u043b\u044e\u0447\u0435\u043d\u0438\u0435\" \u0438 \"\u043f\u0435\u0440\u0435\u0437\u0430\u0433\u0440\u0443\u0437\u043a\u0430\", \u043f\u043e\u0438\u0441\u043a) \u0414\u043b\u044f \u0441\u0435\u0431\u044f \u0440\u0435\u0448\u0438\u043b\u0430, \u0447\u0442\u043e \u043e\u0431\u044f\u0437\u0430\u0442\u0435\u043b\u044c\u043d\u043e \u043f\u0435\u0440\u0435\u043f\u0440\u043e\u0448\u044c\u044e, \u043d\u0435 \u0434\u0435\u043b\u043e \u044d\u0442\u043e, \u0445\u043e\u0434\u0438\u0442\u044c \u0441 \u043d\u0430\u043f\u043e\u043b\u043e\u0432\u0438\u043d\u0443 \u043a\u0438\u0442\u0430\u0439\u0441\u043a\u0438\u043c \u0441\u043e\u0444\u0442\u043e\u043c. \u0421\u043d\u0430\u0447\u0430\u043b\u0430 \u0445\u043e\u0442\u0435\u043b\u0430 \u0441\u0434\u0435\u043b\u0430\u0442\u044c \u044d\u0442\u043e \u0441\u0430\u043c\u0430, \u043d\u0430\u0448\u043b\u0430 \u043c\u043d\u043e\u0436\u0435\u0441\u0442\u0432\u043e \u0432\u0438\u0434\u0435\u043e \u043d\u0430 \u044e\u0442\u0443\u0431\u0435, \u043d\u043e, \u043f\u043e\u0447\u0438\u0442\u0430\u0432 \u043e\u0442\u0437\u044b\u0432\u044b, \u0438\u0441\u043f\u0443\u0433\u0430\u043b\u0430\u0441\u044c, \u0447\u0442\u043e \u043e\u0441\u0442\u0430\u043d\u0443\u0441\u044c \u0441 \"\u043a\u0438\u0440\u043f\u0438\u0447\u043e\u043c\" \u0438 \u043e\u0442\u0434\u0430\u043b\u0430 \u0432 \u0441\u0430\u043b\u043e\u043d \u0440\u0435\u043c\u043e\u043d\u0442\u0430 \u043c\u043e\u0431\u0438\u043b\u044c\u043d\u044b\u0445 - \u043f\u0440\u043e\u0448\u0438\u043b\u0438 m3 mini 5.1.4.1Y \u043d\u0430 5.1.5.1G m3s mini, \u043e\u0431\u043e\u0448\u043b\u043e\u0441\u044c \u0432 700 \u0440\u0443\u0431. \u0422\u0430\u043a \u0447\u0442\u043e \u043d\u0435 \u043f\u043e\u0432\u0442\u043e\u0440\u044f\u0439\u0442\u0435 \u043c\u043e\u0438\u0445 \u043e\u0448\u0438\u0431\u043e\u043a \u0438 \u043f\u0438\u0448\u0438\u0442\u0435 \u043f\u0440\u043e\u0434\u0430\u0432\u0446\u0443 \u0441\u0440\u0430\u0437\u0443 \u0436\u0435. \u0422\u0435\u043b\u0435\u0444\u043e\u043d\u0447\u0438\u043a \u043f\u0440\u0438\u0448\u0435\u043b \u0432\u043e\u0442 \u0432 \u0442\u0430\u043a\u043e\u043c \u0438\u043d\u0442\u0435\u0440\u0435\u0441\u043d\u043e\u043c \u043f\u0430\u043a\u0435\u0442\u0435, \u043e\u043d \u043f\u043e\u043b\u043d\u043e\u0441\u0442\u044c\u044e \u0441\u043e\u0441\u0442\u043e\u0438\u0442 \u0438\u0437 \u043c\u043d\u043e\u0436\u0435\u0441\u0442\u0432\u0430 \u043a\u0430\u043c\u0435\u0440, \u043d\u0430\u043f\u043e\u043b\u043d\u0435\u043d\u043d\u044b\u0445 \u0432\u043e\u0437\u0434\u0443\u0445\u043e\u043c, \u0442\u0430\u043a \u0447\u0442\u043e \u044f \u0437\u0440\u044f \u0431\u043e\u044f\u043b\u0430\u0441\u044c, \u0441 \u043a\u0430\u043a\u043e\u0439 \u0431\u044b \u0441\u0438\u043b\u043e\u0439 \u043f\u043e\u0441\u044b\u043b\u043a\u0443 \u043d\u0435 \u0448\u0432\u044b\u0440\u043d\u0443\u043b\u0438, \u0441 \u043d\u0435\u0439 \u0431\u044b \u0432\u0440\u044f\u0434 \u043b\u0438 \u0447\u0442\u043e-\u0442\u043e \u0441\u043b\u0443\u0447\u0438\u043b\u043e\u0441\u044c. \u0412\u043d\u0443\u0442\u0440\u0438 \u043b\u0435\u0436\u0430\u043b\u0430 \u043a\u0440\u0430\u0441\u0438\u0432\u0430\u044f \u0431\u0435\u043b\u0430\u044f \u043f\u0440\u044f\u043c\u043e\u0443\u0433\u043e\u043b\u044c\u043d\u0430\u044f \u043a\u043e\u0440\u043e\u0431\u043e\u0447\u043a\u0430 \u0441 \u043a\u0438\u0442\u0430\u0439\u0441\u043a\u0438\u043c\u0438 \u0438\u0435\u0440\u043e\u0433\u043b\u0438\u0444\u0430\u043c\u0438, \u043f\u043e\u043b\u043d\u043e\u0441\u0442\u044c\u044e \u0437\u0430\u043f\u0435\u0447\u0430\u0442\u0430\u043d\u043d\u0430\u044f \u0432 \u043f\u043b\u0435\u043d\u043a\u0443. \u041e\u0442\u043a\u0440\u044b\u0432\u0430\u0442\u044c \u0441\u0430\u043c\u0443 \u043a\u043e\u0440\u043e\u0431\u043a\u0443 \u0431\u044b\u043b\u043e \u0441\u043b\u043e\u0436\u043d\u043e\u0432\u0430\u0442\u043e, \u0442\u0430\u043c \u043e\u0442\u0441\u0443\u0442\u0441\u0442\u0432\u0443\u0435\u0442 \u043c\u0435\u0441\u0442\u043e \u043f\u043e\u0434 \u043f\u0430\u043b\u044c\u0446\u044b, \u043d\u0435 \u0437\u0430 \u0447\u0442\u043e \u0443\u0445\u0432\u0430\u0442\u0438\u0442\u044c\u0441\u044f \u0434\u0430\u0436\u0435, \u043d\u043e \u043e\u043d\u0430 \u043c\u043d\u0435 \u0431\u0435\u0437\u0443\u043c\u043d\u043e \u043d\u0440\u0430\u0432\u0438\u0442\u0441\u044f   \u043e\u0447\u0435\u043d\u044c \u043c\u0438\u043d\u0438\u0430\u0442\u044e\u0440\u043d\u0430\u044f \u0438 \u043f\u0440\u0438\u044f\u0442\u043d\u0430\u044f \u043d\u0430 \u043e\u0449\u0443\u043f\u044c. \u0421\u0440\u0430\u0437\u0443 \u043f\u043e\u0434 \u043a\u0440\u044b\u0448\u043a\u043e\u0439 \u043a\u043e\u0440\u043e\u0431\u043a\u0438 \u043b\u0435\u0436\u0430\u043b \u0442\u0435\u043b\u0435\u0444\u043e\u043d, \u043a\u0440\u0430\u0441\u0438\u0432\u043e\u0435 \u0437\u0440\u0435\u043b\u0438\u0449\u0435, \u0432\u044b\u0437\u044b\u0432\u0430\u044e\u0449\u0435\u0435 \u0432\u043e\u0441\u0442\u043e\u0440\u0433! (\u0420\u0430\u0437\u0431\u0438\u0440\u0430\u043b\u0438\u0441\u044c \u0447\u0442\u043e \u0438 \u0433\u0434\u0435 \u0432\u0441\u0435\u0439 \u0441\u0435\u043c\u044c\u0435\u0439.. ) \u0422\u0430\u043a \u0436\u0435 \u0432 \u043a\u043e\u043c\u043f\u043b\u0435\u043a\u0442 \u0432\u0445\u043e\u0434\u0438\u043b\u0438: , \u043f\u043e\u0442\u043e\u043c\u0443 \u043a\u0430\u043a \u044f \u0442\u0443\u043f\u0438\u043b\u0430 \u043c\u0438\u043d\u0443\u0442 20 \ud83d\ude02 \u0418 \u0442\u0430\u043a, \u0438\u0449\u0435\u043c \u043c\u0430\u043b\u0435\u043d\u044c\u043a\u0443\u044e \u0434\u044b\u0440\u043e\u0447\u043a\u0443 \u0441 \u043b\u0435\u0432\u043e\u0439 \u0441\u0442\u043e\u0440\u043e\u043d\u044b, \u0432\u0441\u0442\u0430\u0432\u043b\u044f\u0435\u043c \u0432 \u043d\u0435\u0435 \u043a\u043b\u044e\u0447 \u0438 \u0441\u043b\u0435\u0433\u043a\u0430 \u043d\u0430\u0434\u0430\u0432\u043b\u0438\u0432\u0430\u0435\u043c - \u0432\u044b\u0441\u043a\u0430\u043a\u0438\u0432\u0430\u0435\u0442 \u0441\u043b\u043e\u0442 , \u0432 \u043c\u0430\u0438\u043c\u0435\u043d\u044c\u0448\u0435\u0435 \u043e\u0442\u0432\u0435\u0440\u0441\u0442\u0438\u0435 \u0432\u0441\u0442\u0430\u0432\u043b\u044f\u0435\u0442\u0441\u044f \u041d\u0410\u041d\u041e \u0441\u0438\u043c\u043a\u0430 (\u0441\u0430\u043c\u0430\u044f \u043c\u0430\u043b\u0435\u043d\u044c\u043a\u0430\u044f \u043f\u043e \u0440\u0430\u0437\u043c\u0435\u0440\u0443, \u043a\u0430\u043a\u0430\u044f \u0442\u043e\u043b\u044c\u043a\u043e \u0441\u0443\u0449\u0435\u0441\u0442\u0432\u0443\u0435\u0442), \u0430 \u0440\u044f\u0434\u043e\u043c - \u043d\u0430 \u0432\u0430\u0448 \u0432\u044b\u0431\u043e\u0440: \u043b\u0438\u0431\u043e \u0444\u043b\u0435\u0448\u043a\u0430, \u043b\u0438\u0431\u043e \u0432\u0442\u043e\u0440\u0430\u044f \u0442\u0430\u043a\u0430\u044f \u0436\u0435 \u0441\u0438\u043c\u043a\u0430. ----------------------------------------------------------  --------------------------------------------------------------- \u0412\u043d\u0435\u0448\u043d\u0438\u0439 \u0432\u0438\u0434 \u0433\u0430\u0434\u0436\u0435\u0442\u0430 \u043f\u0440\u0435\u0432\u043e\u0441\u0445\u043e\u0434\u0438\u0442 \u0432\u0441\u0435 \u043e\u0436\u0438\u0434\u0430\u043d\u0438\u044f. \u0412\u043e-\u043f\u0435\u0440\u0432\u044b\u0445, \u043e\u043d \u043e\u0447\u0435\u043d\u044c \u0442\u043e\u043d\u043a\u0438\u0439 - \u0432\u0441\u0435\u0433\u043e 8,3 \u043c\u043c. \u0423\u0434\u043e\u0431\u043d\u043e \u043b\u0435\u0436\u0438\u0442 \u0432 \u0440\u0443\u043a\u0435, \u0430 \u0437\u0430 \u0441\u0447\u0435\u0442 \u0437\u0430\u043a\u0440\u0443\u0433\u043b\u0435\u043d\u043d\u044b\u0445 \u043a\u0440\u0430\u0435\u0432 \u043a\u0430\u0436\u0435\u0442\u0441\u044f \u043d\u0435 \u0442\u0430\u043a\u043e\u0439 \"\u043b\u043e\u043f\u0430\u0442\u043e\u0439\", \u0445\u043e\u0442\u044f \u0438\u043c\u0435\u0435\u0442 \u0440\u0430\u0437\u043c\u0435\u0440 141,5 \u0445 69,5 \u043c\u043c. \u041e\u0447\u0435\u043d\u044c \u0443\u0434\u043e\u0431\u043d\u043e \u043b\u0435\u0436\u0438\u0442 \u0432 \u0440\u0443\u043a\u0435.  \u0414\u0438\u0437\u0430\u0439\u043d \u043f\u0440\u043e\u0441\u0442\u044b\u0445 \u0444\u0443\u043d\u043a\u0446\u0438\u0439 \u043f\u0440\u043e\u0434\u0443\u043c\u0430\u043d \u043e\u0442\u043b\u0438\u0447\u043d\u043e, \u0438\u043c\u0435\u0435\u0442\u0441\u044f \u0432\u0441\u0435\u0433\u043e 1 \u043a\u043d\u043e\u043f\u043a\u0430 (\u043a\u0430\u043a \u0443 \u0430\u0439\u0444\u043e\u043d\u0430, \u0442\u043e\u043b\u044c\u043a\u043e \u043e\u0432\u0430\u043b\u044c\u043d\u0430\u044f), \u043d\u043e \u043e\u043d\u0430, \u043f\u043b\u044e\u0441 \u043a\u043e \u0432\u0441\u0435\u043c\u0443, \u0435\u0449\u0435 \u0438 \u0441\u0435\u043d\u0441\u043e\u0440\u043d\u0430\u044f. \u041f\u0440\u0438 \u043b\u0435\u0433\u043a\u043e\u043c \u043a\u0430\u0441\u0430\u043d\u0438\u0438 - \u0432\u043e\u0437\u0432\u0440\u0430\u0449\u0430\u0435\u0442 \u043d\u0430 \u043e\u0434\u043d\u0443 \u0444\u0443\u043d\u043a\u0446\u0438\u044e \u043d\u0430\u0437\u0430\u0434, \u0430 \u043f\u0440\u0438 \u043d\u0430\u0436\u0430\u0442\u0438\u0438, \u043a\u0430\u043a \u043d\u0430 \u043a\u043d\u043e\u043f\u043a\u0443, \u0432\u044b\u0432\u043e\u0434\u0438\u0442 \"\u0414\u041e\u041c\u041e\u0419\", \u043d\u0430 \u0440\u0430\u0431\u043e\u0447\u0438\u0439 \u0441\u0442\u043e\u043b. \u041e\u0447\u0435\u043d\u044c \u0443\u0434\u043e\u0431\u043d\u043e, \u044f \u043e\u0441\u0432\u043e\u0438\u043b\u0430\u0441\u044c \u0438 \u043f\u0440\u0438\u0432\u044b\u043a\u043b\u0430 \u0437\u0430 \u043f\u0430\u0440\u0443 \u0447\u0430\u0441\u043e\u0432. \u0412\u043d\u0438\u0437\u0443 \u0438\u043c\u0435\u0435\u0442\u0441\u044f \u0433\u043d\u0435\u0437\u0434\u043e \u043f\u043e\u0434 \u0437\u0430\u0440\u044f\u0434\u043d\u043e\u0435 \u0443\u0441\u0442\u0440\u043e\u0439\u0441\u0442\u0432\u043e \u0438 \u043f\u043e 4 \u043e\u0442\u0432\u0435\u0440\u0441\u0442\u0438\u044f \u0434\u0438\u043d\u0430\u043c\u0438\u043a\u0430 \u0441\u043f\u0440\u0430\u0432\u0430 \u0438 \u0441\u043b\u0435\u0432\u0430 \u043e\u0442 \u0437\u0430\u0440\u044f\u0434\u043a\u0438.\u0422\u043e, \u0447\u0442\u043e \u0437\u0430\u0440\u044f\u0434\u043a\u0430 \u0432\u043d\u0438\u0437\u0443, \u043a\u0430\u043a \u043f\u043e \u043c\u043d\u0435, \u043d\u0435 \u0443\u0434\u043e\u0431\u043d\u043e - \u0443 \u043c\u0435\u043d\u044f \u0440\u043e\u0437\u0435\u0442\u043a\u0430 \u043d\u0430\u0445\u043e\u0434\u0438\u0442\u0441\u044f \u043d\u0430\u0434 \u0433\u043e\u043b\u043e\u0432\u043e\u0439 \u0438 \u043e\u0442 \u044d\u0442\u043e\u0433\u043e \u0443\u0436\u0435 \u043d\u0430\u0447\u0430\u043b\u0438 \u043f\u043e\u044f\u0432\u043b\u044f\u0442\u044c\u0441\u044f \u0437\u0430\u043b\u043e\u043c\u044b \u043d\u0430 \u043f\u0440\u043e\u0432\u043e\u0434\u0435. \u0417\u0430\u0434\u043d\u044f\u044f \u043f\u0430\u043d\u0435\u043b\u044c \u0443 \u043c\u0435\u043d\u044f \u0437\u043e\u043b\u043e\u0442\u043e\u0433\u043e \u0446\u0432\u0435\u0442\u0430, \u043c\u0430\u0442\u043e\u0432\u0430\u044f, \u043d\u043e \u044f \u0442\u0430\u043a \u0445\u043e\u0442\u0435\u043b\u0430 \u043d\u0435\u0436\u043d\u043e-\u0440\u043e\u0437\u043e\u0432\u0443\u044e   \u041a \u0441\u043e\u0436\u0430\u043b\u0435\u043d\u0438\u044e, \u0432 \u043d\u0430\u043b\u0438\u0447\u0438\u0438 \u0438\u0445 \u043f\u043e\u0447\u0442\u0438 \u043d\u0438\u0433\u0434\u0435 \u043d\u0435 \u0431\u044b\u043b\u043e, \u0430 \u0435\u0441\u043b\u0438 \u0438 \u0431\u044b\u043b\u0438, \u0442\u043e \u0434\u043e\u0440\u043e\u0436\u0435 \u043f\u043e\u0447\u0442\u0438 \u043d\u0430 1000 \u0440\u0443\u0431. \u041d\u0435 \u043f\u043e\u0439\u043c\u0443, \u0447\u0442\u043e \u0432 \u043d\u0438\u0445 \u043e\u0441\u043e\u0431\u0435\u043d\u043d\u043e\u0433\u043e. \u0414\u0430, \u043a\u0440\u0430\u0441\u043e\u0442\u0430 \u0442\u0440\u0435\u0431\u0443\u0435\u0442 \u0436\u0435\u0440\u0442\u0432, \u043d\u043e \u044d\u0442\u043e \u0447\u0435\u0440\u0435\u0437 \u0447\u0443\u0440. \u0410\u0445 \u0434\u0430, \u0437\u0430\u0431\u044b\u043b\u0430 \u043e \u0433\u043b\u0430\u0432\u043d\u043e\u043c - \u0437\u0430\u0434\u043d\u044f\u044f \u043a\u0440\u044b\u0448\u043a\u0430 \u043f\u0440\u0438\u043a\u0440\u0443\u0447\u0435\u043d\u0430 \u043d\u0430 2 \u0448\u0443\u0440\u0443\u043f\u0430\u0445, \u043a\u0430\u043a \u044f \u043f\u043e\u043d\u0438\u043c\u0430\u044e, \u043e\u0442\u0432\u0435\u0440\u0442\u043a\u0443 \u043d\u0443\u0436\u043d\u043e \u043f\u043e\u043a\u0443\u043f\u0430\u0442\u044c \u0434\u043e\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c\u043d\u043e, \u043a\u0430\u043a \u043f\u043e\u0434 \u0430\u0439\u0444\u043e\u043d (\u0434\u043e\u043f\u043e\u043b\u043d\u0438\u0442\u0435\u043b\u044c\u043d\u0430\u044f \u0432\u044b\u043a\u0430\u0447\u043a\u0430 \u0434\u0435\u043d\u0435\u0433), \u043d\u043e, \u0431\u043b\u0438\u043d, \u0442\u0430\u043a \u0438\u043d\u0442\u0435\u0