\u0441\u0438\u0431\u043e! \u0415\u0449\u0435 \u043e\u0434\u0438\u043d \u0432\u0430\u0436\u043d\u044b\u0439 \u043c\u043e\u043c\u0435\u043d\u0442 - \u044d\u0442\u043e  , \u0434\u0440\u0443\u0433\u0438\u043c\u0438 \u0441\u043b\u043e\u0432\u0430\u043c\u0438 - \u043e\u0444\u043e\u0440\u043c\u043b\u0435\u043d\u0438\u0435, \u0442.\u0435. \u043e\u0431\u043e\u0438 \u0438 \u0442\u0435\u043c\u044b. \u042d\u0442\u0430 \u0444\u0443\u043d\u043a\u0446\u0438\u044f \u0432\u044b\u0437\u0432\u0430\u043b\u0430 \u0443 \u043c\u0435\u043d\u044f \u043f\u0440\u043e\u0441\u0442\u043e \u043f\u043e\u0440\u043e\u0441\u044f\u0447\u0438\u0439 \u0432\u043e\u0441\u0442\u043e\u0440\u0433! \u041f\u043e\u0442\u043e\u043c\u0443 \u043a\u0430\u043a \u0442\u0430\u043c \u0443\u0436\u0435 \u0441\u0440\u0430\u0437\u0443 \u0438\u043c\u0435\u0435\u0442\u0441\u044f \u043e\u0433\u0440\u043e\u043c\u043d\u044b\u0439 \u0432\u044b\u0431\u043e\u0440 \u0442\u0435\u043c \u0438 \u043e\u0431\u043e\u0435\u0432, \u041a\u041e\u0422\u041e\u0420\u042b\u0415 \u041e\u0411\u041d\u041e\u0412\u041b\u042f\u042e\u0422\u0421\u042f \u0427\u0423\u0422\u042c \u041b\u0418 \u041d\u0415 \u041a\u0410\u0416\u0414\u0423\u042e \u041d\u0415\u0414\u0415\u041b\u042e. \u041f\u043b\u044e\u0441 \u043a\u043e \u0432\u0441\u0435\u043c\u0443, \u043a\u0430\u0440\u0442\u0438\u043d\u043a\u0438 \u043e\u0447\u0435\u043d\u044c \u043a\u0440\u0430\u0441\u043e\u0447\u043d\u044b\u0435 \u0438 \u044f\u0440\u043a\u0438\u0435. \u0422\u0435\u043f\u0435\u0440\u044c \u043d\u0435 \u043d\u0443\u0436\u043d\u043e \u0438\u0441\u043a\u0430\u0442\u044c \u0438 \u0441\u043a\u0430\u0447\u0438\u0432\u0430\u0442\u044c \u0442\u0435\u043c\u044b \u0432 \u0438\u043d\u0442\u0435\u0440\u043d\u0435\u0442\u0435 \u0441 \u043d\u0435\u0438\u0437\u0432\u0435\u0441\u0442\u043d\u044b\u0445, \u043f\u043e\u0442\u0435\u043d\u0446\u0438\u0430\u043b\u044c\u043d\u043e \u043e\u043f\u0430\u0441\u043d\u044b\u0445 \u0441\u0430\u0439\u0442\u043e\u0432, \u0430 \u043f\u0440\u043e\u0441\u0442\u043e \u0437\u0430\u0439\u0442\u0438 \u0432 \u043d\u0443\u0436\u043d\u0443\u044e \u043f\u0440\u043e\u0433\u0440\u0430\u043c\u043c\u0443 \u043d\u0430 \u0440\u0430\u0431\u043e\u0447\u0435\u043c \u0441\u0442\u043e\u043b\u0435. \u0415\u0449\u0435 \u0432 \u043d\u0430\u0441\u0442\u0440\u043e\u0439\u043a\u0430\u0445 \u0438\u043c\u0435\u0435\u0442\u0441\u044f \u0442\u0430\u043a\u0430\u044f \u0444\u0443\u043d\u043a\u0446\u0438\u044f \u043a\u0430\u043a \"\u042d\u043d\u0435\u0440\u0433\u043e\u043f\u043e\u0442\u0440\u0435\u0431\u043b\u0435\u043d\u0438\u0435\", \u0442\u0430\u043c \u043c\u043e\u0436\u043d\u043e \u0432\u044b\u0431\u0440\u0430\u0442\u044c \u0443\u0434\u043e\u0431\u043d\u044b\u0439 \u0434\u043b\u044f \u0432\u0430\u0441 \u0440\u0435\u0436\u0438\u043c - \u043b\u0438\u0431\u043e \u044d\u043d\u0435\u0440\u0433\u043e\u0441\u0431\u0435\u0440\u0435\u0436\u0435\u043d\u0438\u0435 \u0431\u0435\u0437 \u0441\u043e\u0435\u0434\u0438\u043d\u0435\u043d\u0438\u044f \u0438 \u043f\u0440\u043e\u0447\u0435\u0433\u043e, \u0447\u0442\u043e \u043d\u0435\u0440\u0435\u0430\u043b\u044c\u043d\u043e \u0431\u044b\u0441\u0442\u0440\u043e \u0441\u0430\u0436\u0430\u0435\u0442 \u0431\u0430\u0442\u0430\u0440\u0435\u044e, \u043b\u0438\u0431\u043e, \u043d\u0430\u043e\u0431\u043e\u0440\u043e\u0442, \u0432\u044b\u0431\u0440\u0430\u0442\u044c \u0432\u0441\u0435 \u043f\u043e \u043c\u0430\u043a\u0441\u0438\u043c\u0443\u043c\u0443, \u0442\u043e\u0433\u0434\u0430 \u0437\u0430\u0440\u044f\u0434\u0430 \u0445\u0432\u0430\u0442\u0438\u0442 \u0447\u0430\u0441\u043e\u0432 \u043d\u0430 7-8. \u0418 \u0415\u0429\u0415 \u041e\u0414\u0418\u041d \u041d\u0415\u041c\u0410\u041b\u041e\u0412\u0410\u0416\u041d\u042b\u0419 \u041c\u041e\u041c\u0415\u041d\u0422 -  \u0422\u0443\u043f\u0438\u043b\u0430 \u044f \u0434\u043e\u0432\u043e\u043b\u044c\u043d\u043e \u0434\u043e\u043b\u0433\u043e, \u0433\u0434\u0435 \u0435\u0433\u043e \u043d\u0430\u0439\u0442\u0438? \u0412\u0435\u0434\u044c \u043a\u043d\u043e\u043f\u043a\u0430 \u0442\u043e \u0432\u0441\u0435\u0433\u043e \u043e\u0434\u043d\u0430, \u0434\u0430 \u0435\u0449\u0435 \u0438 2 \u0432 1, \u043a\u0430\u043a \u0442\u043e\u0433\u0434\u0430 \u0437\u0430\u043a\u0440\u044b\u0432\u0430\u0442\u044c \u043f\u0440\u0438\u043b\u043e\u0436\u0435\u043d\u0438\u044f?! \u0410 \u0432\u0441\u0435 \u043e\u043a\u0430\u0437\u0430\u043b\u043e\u0441\u044c \u043d\u0430\u043c\u043d\u043e\u0433\u043e \u043f\u0440\u043e\u0449\u0435 - \u041f\u0420\u041e\u0421\u0422\u041e \u041f\u0420\u041e\u0412\u0415\u0421\u0422\u0418 \u0421\u041d\u0418\u0417\u0423 \u0412\u0412\u0415\u0420\u0425 \u0421\u041f\u0420\u0410\u0412\u0410 \u0418\u041b\u0418 \u0421\u041b\u0415\u0412\u0410 \u041e\u0422 \u041a\u041d\u041e\u041f\u041a\u0418 \u0414\u041e \u0421\u0415\u0420\u0415\u0414\u0418\u041d\u042b \u042d\u041a\u0420\u0410\u041d\u0410. \u041e\u0442\u043a\u0440\u043e\u0435\u0442\u0441\u044f \u0434\u0438\u0441\u043f\u0435\u0442\u0447\u0435\u0440 \u0441 \u0430\u043a\u0442\u0438\u0432\u043d\u044b\u043c\u0438 \u043f\u0440\u0438\u043b\u043e\u0436\u0435\u043d\u0438\u044f\u043c\u0438, \u043a\u0430\u043a \u0443 \u0430\u0439\u0444\u043e\u043d\u0430, \u0434\u043e\u043f\u043e\u043b\u043d\u0435\u043d\u043d\u044b\u0439 \u0432\u0435\u0440\u0445\u043d\u0435\u0439 \u0443\u0434\u043e\u0431\u043d\u043e\u0439 \u043a\u043d\u043e\u043f\u043a\u043e\u0439 \"\u0417\u0430\u043a\u0440\u044b\u0442\u044c \u0432\u0441\u0435\". \u0422\u0430\u043a \u0436\u0435 \u043e\u043d\u0430 \u043e\u0447\u0438\u0449\u0430\u0435\u0442 \u043f\u0430\u043c\u044f\u0442\u044c. \u0420\u043e\u0434\u043d\u0430\u044f \u043a\u043b\u0430\u0432\u0438\u0430\u0442\u0443\u0440\u0430 \u0431\u044b\u043b\u0430 \u0443\u0436\u0430\u0441\u043d\u043e\u0439, \u043d\u0435\u0443\u0434\u043e\u0431\u043d\u043e \u043f\u0435\u0440\u0435\u043a\u043b\u044e\u0447\u0430\u043b\u0430\u0441\u044c \u043c\u0435\u0436\u0434\u0443 \u044f\u0437\u044b\u043a\u0430\u043c\u0438, \u0438 \u0437\u0430\u0433\u043b\u0430\u0432\u043d\u044b\u0435 \u0431\u0443\u043a\u0432\u044b \u043c\u043e\u0436\u043d\u043e \u0431\u044b\u043b\u043e \u043e\u043f\u0440\u0435\u0434\u0435\u043b\u0438\u0442\u044c \u0442\u043e\u043b\u044c\u043a\u043e \u043f\u043e \u0441\u0442\u0440\u0435\u043b\u043e\u0447\u043a\u0435 \u0441\u0431\u043e\u043a\u0443, \u043f\u043e\u044d\u0442\u043e\u043c\u0443 \u043f\u0440\u0438\u0448\u043b\u043e\u0441\u044c \u0443\u0441\u0442\u0430\u043d\u0430\u0432\u043b\u0438\u0432\u0430\u0442\u044c \u043f\u0440\u0438\u043b\u043e\u0436\u0435\u043d\u0438\u0435 \"\u041a\u043b\u0430\u0432\u0438\u0430\u0442\u0443\u0440\u0430 Google\" \u041d\u0430 \u0440\u0430\u0431\u043e\u0447\u0435\u043c \u0441\u0442\u043e\u043b\u0435 \u0441\u043b\u0443\u0447\u0430\u0439\u043d\u043e \u043e\u0431\u043d\u0430\u0440\u0443\u0436\u0438\u043b\u0430 \u044f\u0440\u043b\u044b\u043a \"\u041f\u043e\u043b\u0435\u0437\u043d\u043e\u0435\", \u0447\u0442\u043e \u0442\u043e\u043b\u044c\u043a\u043e \u043a\u0438\u0442\u0430\u0439\u0446\u044b \u043d\u0435 \u043f\u0440\u0438\u0434\u0443\u043c\u0430\u044e\u0442!   \u0410 \u0443 \u0432\u0430\u0441 \u0432 \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0435 \u0435\u0441\u0442\u044c \u0423\u0440\u043e\u0432\u0435\u043d\u044c \u0438\u043b\u0438 \u041b\u0438\u043d\u0435\u0439\u043a\u0430? \u0410 \u043c\u043e\u0436\u0435\u0442 \u041b\u0443\u043f\u0430? \u041d\u043e \u044d\u0442\u043e \u043f\u0440\u043e\u0441\u0442\u043e \u0443\u0432\u0435\u043b\u0438\u0447\u0435\u043d\u0438\u0435 \u043a\u0430\u043c\u0435\u0440\u044b, \u0430 \u0437\u0435\u0440\u043a\u0430\u043b\u043e - \u0444\u0440\u043e\u043d\u0442\u0430\u043b\u044c\u043d\u0430\u044f \u043a\u0430\u043c\u0435\u0440\u0430.  \u041d\u0443 \u0438 \u043f\u043e \u043c\u0435\u043b\u043e\u0447\u0438 - \u043f\u0440\u043e\u0441\u0442\u043e \u043a\u0440\u0430\u0441\u0438\u0432\u043e\u0435 \u043e\u0444\u043e\u0440\u043c\u043b\u0435\u043d\u0438\u0435 \"\u041f\u0440\u043e\u0432\u043e\u0434\u043d\u0438\u043a\u0430\", \u043a\u043e\u043d\u0442\u0430\u043a\u0442\u043e\u0432 \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0430 \u0438 \u043d\u0430\u0441\u0442\u0440\u043e\u0435\u043a: \u0418, \u0432 \u0437\u0430\u043a\u043b\u044e\u0447\u0435\u043d\u0438\u0435, \u0445\u043e\u0442\u0435\u043b\u043e\u0441\u044c \u0431\u044b \u0432\u044b\u0434\u0435\u043b\u0438\u0442\u044c \u043b\u0438\u0447\u043d\u043e \u0434\u043b\u044f \u0441\u0435\u0431\u044f \u043d\u0435\u043a\u043e\u0442\u043e\u0440\u044b\u0435 \u043c\u0438\u043d\u0443\u0441\u044b \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0430: \u0414\u0430, \u043a\u0430\u0447\u0435\u0441\u0442\u0432\u043e \u0444\u043e\u0442\u043e \u0434\u043e\u0432\u043e\u043b\u044c\u043d\u043e \u0445\u043e\u0440\u043e\u0448\u0435\u0435, \u043d\u043e \u043d\u0435 13 \u041c\u041f, \u043a\u0430\u043a \u0437\u0430\u044f\u0432\u043b\u044f\u044e\u0442 \u043f\u0440\u043e\u0434\u0430\u0432\u0446\u044b. \u041e\u0442 \u0441\u0438\u043b\u044b \u0438\u0445 \u0442\u0430\u043c 8-10. \u0412 \u0442\u0435\u043c\u043d\u043e\u0442\u0435 \u0441\u043e \u0432\u0441\u043f\u044b\u0448\u043a\u043e\u0439 \u043f\u043e\u043b\u0443\u0447\u0430\u0435\u0442\u0441\u044f \u0442\u043e\u0436\u0435 \u043d\u0435\u043f\u043b\u043e\u0445\u043e. \u0421\u043d\u0430\u0447\u0430\u043b\u0430, \u043f\u0440\u0438 \u0441\u0430\u043c\u043e\u0439 \u043f\u0435\u0440\u0432\u043e\u0439 \u043f\u0440\u043e\u0432\u0435\u0440\u043a\u0435 \u043a\u0430\u043c\u0435\u0440\u044b \u043e\u043d\u0430 \u043e\u043e\u043e\u043e\u0447\u0435\u043d\u044c \u0441\u0438\u043b\u044c\u043d\u043e \u0441\u043c\u0430\u0437\u044b\u0432\u0430\u043b\u0430. \u0414\u043e \u043d\u0430\u0436\u0430\u0442\u0438\u044f \u043a\u043d\u043e\u043f\u043a\u0438 \"\u0444\u043e\u0442\u043e\" \u0438\u0437\u043e\u0431\u0440\u0430\u0436\u0435\u043d\u0438\u0435 \u0431\u044b\u043b\u043e \u0441\u0443\u043f\u0435\u0440\u0447\u0435\u0442\u043a\u043e\u0435, \u0430 \u043f\u043e\u0441\u043b\u0435 - \u043a\u0430\u043a \u0431\u0443\u0434\u0442\u043e \u0432 \u0444\u043e\u0442\u043e\u0448\u043e\u043f\u0435 \u043d\u0430\u043b\u043e\u0436\u0438\u043b\u0438 \u044d\u0444\u0444\u0435\u043a\u0442 \u0440\u0430\u0437\u043c\u044b\u0442\u0438\u044f, \u043f\u043e\u0441\u043c\u043e\u0442\u0440\u0438\u0442\u0435 \u0441\u0430\u043c\u0438: \u0421\u043a\u0430\u0437\u0430\u0442\u044c, \u0447\u0442\u043e \u044f \u0440\u0430\u0441\u0441\u0442\u0440\u043e\u0438\u043b\u0430\u0441\u044c?! \u042d\u0442\u043e \u043d\u0438\u0447\u0435\u0433\u043e \u043d\u0435 \u0441\u043a\u0430\u0437\u0430\u0442\u044c, \u0432\u0435\u0434\u044c \u043f\u043e \u0441\u0443\u0442\u0438, \u0438\u0437\u043d\u0430\u0447\u0430\u043b\u044c\u043d\u043e, \u044d\u0442\u043e \u0431\u044b\u043b\u043e \u043e\u0434\u043d\u0438\u043c \u0438\u0437 \u0441\u0430\u043c\u044b\u0445 \u0433\u043b\u0430\u0432\u043d\u044b\u0445 \u043a\u0440\u0438\u0442\u0435\u0440\u0438\u0435\u0432 \u0432 \u0438\u0434\u0435\u0430\u043b\u044c\u043d\u043e\u043c \u0434\u043b\u044f \u043c\u0435\u043d\u044f \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0435. \u041d\u043e, \u043f\u043e\u0442\u043e\u043c \u0434\u043e \u043c\u0435\u043d\u044f, \u043a\u0430\u043a \u0434\u043e \u0443\u0442\u043a\u0438, \u043d\u0430 3 \u0441\u0443\u0442\u043a\u0438 \u0434\u043e\u0448\u043b\u043e, \u0447\u0442\u043e \u0434\u0435\u043b\u043e \u0431\u044b\u043b\u043e \u0432 \u0437\u0430\u0434\u043d\u0435\u0439 \u043f\u043b\u0435\u043d\u043a\u0435, \u043a\u043e\u0442\u043e\u0440\u0443\u044e \u044f \u043e\u0442\u043a\u043b\u0435\u0438\u0432\u0430\u0442\u044c \u0441\u0440\u0430\u0437\u0443 \u043d\u0435 \u0441\u0442\u0430\u043b\u0430 - \u0436\u0434\u0430\u043b\u0430 \u0431\u0430\u043c\u043f\u0435\u0440 \u0441 \u0410\u043b\u0438, \u043a\u043e\u0442\u043e\u0440\u044b\u0439 \u043f\u0440\u0435\u0434\u0441\u0442\u0430\u0432\u043b\u0435\u043d \u0432\u044b\u0448\u0435. \u0418 \u0442\u0430\u043a, \u043f\u043e\u0440\u0430 \u0437\u0430\u043a\u0430\u043d\u0447\u0438\u0432\u0430\u0442\u044c \u0441\u0432\u043e\u044e \u0431\u043e\u043b\u0442\u043e\u0432\u043d\u044e, \u0438 \u043f\u043e\u0434\u0432\u043e\u0434\u0438\u0442\u044c \u0438\u0442\u043e\u0433\u0438 - \u043e\u043d\u0438 \u0442\u0430\u043a\u043e\u0432\u044b: \u0442\u0435\u043b\u0435\u0444\u043e\u043d \u0441\u0443\u043f\u0435\u0440\u043c\u0435\u0433\u0430\u043d\u0435\u0440\u0435\u0430\u043b\u044c\u043d\u043e \u043a\u0440\u0443\u0442\u043e\u0439, \u043f\u0440\u0435\u0432\u0437\u043e\u0448\u0435\u043b \u0432\u0441\u0435 \u043c\u043e\u0438 \u043e\u0436\u0438\u0434\u0430\u043d\u0438\u044f \u043a\u0430\u043a \u043f\u043e \u0432\u043d\u0435\u0448\u043d\u0435\u043c\u0443 \u0432\u0438\u0434\u0443, \u0442\u0430\u043a \u0438 \u043f\u043e \u043d\u0430\u0447\u0438\u043d\u043a\u0435. \u041d\u0435 \u0431\u043e\u0439\u0442\u0435\u0441\u044c \u0437\u0430\u043a\u0430\u0437\u044b\u0432\u0430\u0442\u044c \u0443 \u043a\u0438\u0442\u0430\u0439\u0446\u0435\u0432 - \u0431\u043e\u043b\u044c\u0448\u0438\u043d\u0441\u0442\u0432\u043e \u0438\u0445 \u043f\u0440\u043e\u0434\u0443\u043a\u0446\u0438\u0438 \u043d\u0430\u043c\u043d\u043e\u0433\u043e \u043b\u0443\u0447\u0448\u0435 \u043d\u0430\u0448\u0435\u0439, \u0434\u0430 \u0438 \u0432 \u043a\u043e\u043d\u0446\u0435 \u043a\u043e\u043d\u0446\u043e\u0432 \u0432 \u0441\u0430\u043b\u043e\u043d\u0430\u0445 \u0441\u0432\u044f\u0437\u0438 \u043e\u043d\u0438 \u0432\u0441\u0435 \u0440\u0430\u0432\u043d\u043e \u0432\u0441\u0435 \u0438\u0437 \u041a\u0438\u0442\u0430\u044f, \u043d\u043e \u0441 \u043e\u0433\u0440\u043e\u043c\u043d\u043e\u0439 \u043d\u0430\u043a\u0440\u0443\u0442\u043a\u043e\u0439, \u0430 \u0437\u0430\u0447\u0435\u043c \u043f\u043b\u0430\u0442\u0438\u0442\u044c \u0431\u043e\u043b\u044c\u0448\u0435?! \u041e\u0434\u043d\u043e\u0437\u043d\u0430\u0447\u043d\u043e, \u0420\u0415\u041a\u041e\u041c\u0415\u041d\u0414\u0423\u042e! \u041d\u0430\u0434\u0435\u044e\u0441\u044c, \u0431\u044b\u043b\u0430 \u043f\u043e\u043b\u0435\u0437\u043d\u043e\u0439. \u0421\u043f\u0430\u0441\u0438\u0431\u043e \u0437\u0430 \u0432\u043d\u0438\u043c\u0430\u043d\u0438\u0435. \u0412\u0430\u0448\u0430  \u273d \u273d \u273d \u273d \u273d"}
{"rating": "5", "text": "\u041d\u0435 \u0442\u0430\u043a \u0434\u0430\u0432\u043d\u043e \u044f \u0441\u0442\u043e\u044f\u043b\u0430 \u043f\u0435\u0440\u0435\u0434 \u0432\u044b\u0431\u043e\u0440\u043e\u043c \u043d\u043e\u0432\u043e\u0433\u043e \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d\u0430. \u041f\u0440\u043e\u0448\u043b\u044b\u0439 \u043c\u043e\u0439 \u043b\u044e\u0431\u0438\u043c\u0447\u0438\u043a Explay Communicator \u0437\u0430 3 \u0433\u043e\u0434\u0430 \u0443\u0441\u0442\u0430\u0440\u0435\u043b, \u0438 \u043d\u0443\u0436\u043d\u043e \u0431\u044b\u043b\u043e \u0431\u0440\u0430\u0442\u044c \u0447\u0442\u043e-\u0442\u043e \u0431\u043e\u043b\u0435\u0435 \u043c\u043e\u0449\u043d\u043e\u0435, \u0448\u0443\u0441\u0442\u0440\u043e\u0435 \u0438 \u0436\u0435\u043b\u0430\u0442\u0435\u043b\u044c\u043d\u043e \u043a\u0440\u0430\u0441\u0438\u0432\u043e\u0435. \u041f\u043e\u0441\u043b\u0435 \u0434\u043e\u043b\u0433\u0438\u0445 \u043f\u043e\u0438\u0441\u043a\u043e\u0432 \u0432\u044b\u0431\u0440\u0430\u043b\u0430 \u0441\u0435\u0431\u0435 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d, \u043d\u043e \u0446\u0435\u043d\u043d\u0438\u043a \u0435\u0433\u043e \u0431\u044b\u043b \u0432 \u0440\u0430\u0439\u043e\u043d\u0435 34 \u0442\u044b\u0441. \u0418 \u0442\u0443\u0442 \u0437\u0430\u0441\u0430\u0434\u0430.. \u041f\u043b\u0430\u0442\u0438\u0442\u044c \u0437\u0430 \u0441\u043c\u0430\u0440\u0442 25-30 \u0442\u044b\u0441 \u0438 \u0431\u043e\u043b\u0435\u0435 \u043d\u0435 \u043e\u0447\u0435\u043d\u044c \u0445\u043e\u0442\u0435\u043b\u043e\u0441\u044c (\u0442\u0435, \u0443 \u043a\u043e\u0433\u043e \u0435\u0441\u0442\u044c \u043c\u0430\u043b\u0435\u043d\u044c\u043a\u0438\u0435 \u0434\u0435\u0442\u0438, \u043f\u043e\u0439\u043c\u0443\u0442 \u043c\u0435\u043d\u044f=)), \u0430 \u0432\u0441\u0435, \u0447\u0442\u043e \u0441\u0442\u043e\u0438\u043b\u043e \u0434\u0435\u0448\u0435\u0432\u043b\u0435, \u0438\u043b\u0438 \u043f\u043e \u0436\u0435\u043b\u0435\u0437\u0443 \u0431\u044b\u043b\u043e \u0441\u043b\u0430\u0431\u0435\u0435, \u0438\u043b\u0438 \u0434\u0438\u0430\u0433\u043e\u043d\u0430\u043b\u044c \u0431\u044b\u043b\u0430 \u043c\u0430\u043b\u0435\u043d\u044c\u043a\u043e\u0439. \u0410 \u043d\u0430 \u0434\u0438\u0430\u0433\u043e\u043d\u0430\u043b\u044c \u043c\u0435\u043d\u0435\u0435 6-\u0442\u0438 \u0434\u044e\u0439\u043c\u043e\u0432 \u044f \u0431\u044b\u043b\u0430 \u0443\u0436\u0435 \u043d\u0435 \u0441\u043e\u0433\u043b\u0430\u0441\u043d\u0430, \u043f\u043e\u044d\u0442\u043e\u043c\u0443 \u043f\u0440\u0438\u0448\u043b\u043e\u0441\u044c \u0442\u0449\u0430\u0442\u0435\u043b\u044c\u043d\u0435\u0435 \u043f\u043e\u0438\u0441\u043a\u0430\u0442\u044c \u0447\u0442\u043e-\u043d\u0438\u0431\u0443\u0434\u044c \u0434\u0440\u0443\u0433\u043e\u0435.. \u041e\u0442\u0441\u043e\u0440\u0442\u0438\u0440\u043e\u0432\u0430\u0432 \u0432 \u043e\u0447\u0435\u0440\u0435\u0434\u043d\u043e\u0439 \u0440\u0430\u0437 \u043d\u0430 \u043c\u0430\u0440\u043a\u0435\u0442\u0435 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d\u044b \u043f\u043e \u043d\u0443\u0436\u043d\u044b\u043c \u043c\u043d\u0435 \u043f\u0430\u0440\u0430\u043c\u0435\u0442\u0440\u0430\u043c \u044f \u0443\u0432\u0438\u0434\u0435\u043b\u0430 \u0432 \u0441\u043f\u0438\u0441\u043a\u0435 \u0435\u0433\u043e - Xiaomi Mi Max! \u0412\u043d\u0435\u0448\u043d\u0435 \u043f\u043e\u043d\u0440\u0430\u0432\u0438\u043b\u0441\u044f \u0441\u0440\u0430\u0437\u0443, \u0438 \u043f\u043e \u0445\u0430\u0440\u0430\u043a\u0442\u0435\u0440\u0438\u0441\u0442\u0438\u043a\u0430\u043c \u0443\u0441\u0442\u0440\u043e\u0438\u043b \u043f\u043e\u043b\u043d\u043e\u0441\u0442\u044c\u044e, \u043d\u043e \u0441\u043b\u0435\u0433\u043a\u0430 \u0441\u043c\u0443\u0442\u0438\u043b\u0438 \u0435\u0433\u043e \u0433\u0430\u0431\u0430\u0440\u0438\u0442\u044b.. \u0414\u0438\u0430\u0433\u043e\u043d\u0430\u043b\u044c \u044d\u043a\u0440\u0430\u043d\u0430 \u0430\u0436 6,44 \u0434\u044e\u0439\u043c\u0430.. \u041c\u043d\u0435 \u043e\u043d \u043f\u043e\u043a\u0430\u0437\u0430\u043b\u0441\u044f \u0441\u043b\u0438\u0448\u043a\u043e\u043c \u0431\u043e\u043b\u044c\u0448\u0438\u043c, \u0438 \u0432\u043e\u0437\u043d\u0438\u043a\u043b\u0438 \u0441\u043e\u043c\u043d\u0435\u043d\u0438\u044f \u043d\u0430 \u0442\u0435\u043c\u0443 \u0442\u043e\u0433\u043e, \u043a\u0430\u043a \u0438\u043c \u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u0442\u044c\u0441\u044f \u0434\u0435\u0432\u0443\u0448\u043a\u0435. \u0414\u0430, \u043f\u0430\u043b\u044c\u0446\u044b \u0443 \u043c\u0435\u043d\u044f \u0434\u043b\u0438\u043d\u043d\u044b\u0435 (\u043c\u0443\u0437\u044b\u043a\u0430\u043b\u044c\u043d\u044b\u0435, \u043a\u0430\u043a \u0438\u0445 \u043d\u0430\u0437\u044b\u0432\u0430\u044e\u0442), \u043d\u043e \u0442\u0435\u043c \u043d\u0435 \u043c\u0435\u043d\u0435\u0435 \u043b\u0430\u0434\u043e\u0448\u043a\u0430 \u0443 \u043c\u0435\u043d\u044f \u0432\u0441\u0435 \u0442\u0430\u043a\u0438 \u0436\u0435\u043d\u0441\u043a\u0430\u044f, \u0441\u0440\u0435\u0434\u043d\u0438\u0445 \u0440\u0430\u0437\u043c\u0435\u0440\u043e\u0432. \u041f\u043e\u043b\u0430\u0437\u0438\u0432 \u043f\u043e \u0438\u043d\u0442\u0435\u0440\u043d\u0435\u0442\u0443 \u0438 \u043f\u043e\u0438\u0441\u043a\u0430\u0432 \u0447\u0442\u043e-\u043d\u0438\u0431\u0443\u0434\u044c \u0435\u0449\u0435, \u043f\u043e\u0441\u043c\u043e\u0442\u0440\u0435\u0432 \u043a\u0443\u0447\u0443 \u043e\u0431\u0437\u043e\u0440\u043e\u0432 \u0438 \u043f\u0435\u0440\u0435\u0447\u0438\u0442\u0430\u0432 \u043c\u0430\u0441\u0441\u0443 \u0441\u0442\u0430\u0442\u0435\u0439, \u0430\u043b\u044c\u0442\u0435\u0440\u043d\u0430\u0442\u0438\u0432\u0430 Max'\u0443 \u0442\u0430\u043a \u0438 \u043d\u0435 \u0431\u044b\u043b\u0430 \u043d\u0430\u0439\u0434\u0435\u043d\u0430. \u0418 \u044f \u0432\u0441\u0435 \u0442\u0430\u043a\u0438 \u0440\u0435\u0448\u0438\u043b\u0430\u0441\u044c \u043d\u0430 \u0435\u0433\u043e \u043f\u043e\u043a\u0443\u043f\u043a\u0443)) \u041f\u043b\u044e\u0441\u043e\u043c \u0431\u044b\u043b\u043e \u0435\u0449\u0435 \u0442\u043e, \u0447\u0442\u043e \u0430\u043f\u043f\u0430\u0440\u0430\u0442 \u0434\u043e\u0432\u043e\u043b\u044c\u043d\u043e \u0441\u0432\u0435\u0436\u0435\u043d\u044c\u043a\u0438\u0439, \u0434\u0430\u0442\u0430 \u0430\u043d\u043e\u043d\u0441\u0438\u0440\u043e\u0432\u0430\u043d\u0438\u044f \u0434\u0430\u043d\u043d\u043e\u0439 \u043c\u043e\u0434\u0435\u043b\u044c\u043a\u0438 - \u043c\u0430\u0439 2016 \u0433\u043e\u0434\u0430. \u041f\u0440\u043e\u043c\u043e\u043d\u0438\u0442\u043e\u0440\u0438\u043b\u0430 \u043c\u0430\u0433\u0430\u0437\u0438\u043d\u044b \u0443 \u043d\u0430\u0441 \u0432 \u0433\u043e\u0440\u043e\u0434\u0435, \u0446\u0435\u043d\u043d\u0438\u043a \u043d\u0430 Max'\u0430 \u0431\u044b\u043b \u043d\u0435\u043e\u0431\u043e\u0441\u043d\u043e\u0432\u0430\u043d\u043d\u043e \u0437\u0430\u0432\u044b\u0448\u0435\u043d\u043d\u044b\u043c, \u043f\u043e\u044d\u0442\u043e\u043c\u0443 \u043c\u044b \u0441 \u043c\u0443\u0436\u0435\u043c, \u043f\u043e\u0441\u043e\u0432\u0435\u0449\u0430\u0432\u0448\u0438\u0441\u044c, \u0437\u0430\u043a\u0430\u0437\u0430\u043b\u0438 \u0435\u0433\u043e \u0438\u0437 \u041c\u043e\u0441\u043a\u0432\u044b, \u0438 \u043f\u043e \u043f\u0440\u043e\u0448\u0435\u0441\u0442\u0432\u0438\u044e 4-\u0445 \u0434\u043d\u0435\u0439 \u044f \u0443\u0436\u0435 \u0441\u0442\u043e\u044f\u043b\u0430 \u0432 \u0442\u0435\u0440\u043c\u0438\u043d\u0430\u043b\u0435 CDEK'\u0430 \u0438 \u0437\u0430\u0431\u0438\u0440\u0430\u043b\u0430 \u0441\u0432\u043e\u044e \"\u043b\u043e\u043f\u0430\u0442\u043e\u0447\u043a\u0443\"..) \u0418 \u0432\u043e\u0442 \u043e\u043d, \u043c\u043e\u0439 \u043a\u0440\u0430\u0441\u0430\u0432\u0435\u0446 \u0441 \u0441\u043e\u0447\u043d\u044b\u043c \u0438 \u044f\u0440\u043a\u0438\u043c \u0434\u0438\u0441\u043f\u043b\u0435\u0435\u043c))) \u0422\u043e\u043d\u0435\u043d\u044c\u043a\u0438\u0435 \u0440\u0430\u043c\u043e\u0447\u043a\u0438, \u0441\u043b\u0435\u0433\u043a\u0430 \u0441\u043a\u043e\u0448\u0435\u043d\u043d\u044b\u0435 \u0433\u0440\u0430\u043d\u0438 (\u043d\u0435 \u0437\u0430\u043a\u0440\u0443\u0433\u043b\u0435\u043d\u043d\u044b\u0435), \u0441\u0442\u0435\u043a\u043b\u043e 2.5D.. \u041e\u043d \u043f\u0440\u043e\u0441\u0442\u043e \u0448\u0438\u043a\u0430\u0440\u043d\u044b\u0439!)) \u0421\u043d\u0430\u0447\u0430\u043b\u0430, \u043a\u043e\u0433\u0434\u0430 \u044f \u0442\u043e\u043b\u044c\u043a\u043e \u043e\u0442\u043a\u0440\u044b\u043b\u0430 \u043a\u043e\u0440\u043e\u0431\u043a\u0443, \u043a\u043e\u043d\u0435\u0447\u043d\u043e \u0436\u0435 \u0431\u044b\u043b \u043b\u0435\u0433\u043a\u0438\u0439 \u0448\u043e\u043a \u043e\u0442 \u0435\u0433\u043e \u0433\u0430\u0431\u0430\u0440\u0438\u0442\u043e\u0432.. \u0421 \u043e\u0431\u0440\u0430\u0442\u043d\u043e\u0439 \u0441\u0442\u043e\u0440\u043e\u043d\u044b \u043a\u043e\u0440\u043e\u0431\u043e\u0447\u043a\u0438 \u0431\u044b\u043b\u0438 \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u044b \u043a\u0440\u0430\u0442\u043a\u0438\u0435 \u0445\u0430\u0440\u0430\u043a\u0442\u0435\u0440\u0438\u0441\u0442\u0438\u043a\u0438 Mi Max \u043f\u0440\u043e\u0434\u0430\u0435\u0442\u0441\u044f \u0432 \u0447\u0435\u0442\u044b\u0440\u0435\u0445 \u043c\u043e\u0434\u0438\u0444\u0438\u043a\u0430\u0446\u0438\u044f\u0445: 2/16, 3/32, 3/64 \u0438 4/128, \u0433\u0434\u0435 \u043f\u0435\u0440\u0432\u0430\u044f \u0446\u0438\u0444\u0440\u0430 \u043e\u0431\u043e\u0437\u043d\u0430\u0447\u0430\u0435\u0442 \u043e\u0431\u044a\u0435\u043c \u043e\u043f\u0435\u0440\u0430\u0442\u0438\u0432\u043d\u043e\u0439 \u043f\u0430\u043c\u044f\u0442\u0438, \u0430 \u0432\u0442\u043e\u0440\u0430\u044f \u043e\u0431\u044a\u0435\u043c \u043f\u043e\u0441\u0442\u043e\u044f\u043d\u043d\u043e\u0439 \u0434\u043b\u044f \u0445\u0440\u0430\u043d\u0435\u043d\u0438\u044f \u0434\u0430\u043d\u043d\u044b\u0445. \u0410 \u0442\u0430\u043a \u0436\u0435 \u043f\u043e\u0441\u0442\u0430\u0432\u043b\u044f\u0435\u0442\u0441\u044f \u0432 \u0442\u0440\u0435\u0445 \u0446\u0432\u0435\u0442\u0430\u0445: \u0437\u043e\u043b\u043e\u0442\u0438\u0441\u0442\u044b\u0439, \u0441\u0435\u0440\u0435\u0431\u0440\u0438\u0441\u0442\u044b\u0439 \u0438 \u0441\u0435\u0440\u044b\u0439. \u041c\u043e\u0439 \u044d\u043a\u0437\u0435\u043c\u043f\u043b\u044f\u0440 \u0437\u043e\u043b\u043e\u0442\u0438\u0441\u0442\u043e\u0433\u043e \u0446\u0432\u0435\u0442\u0430 \u0432 \u043c\u043e\u0434\u0438\u0444\u0438\u043a\u0430\u0446\u0438\u0438 3/32. \u041f\u0440\u0438\u0447\u0435\u043c \u0442\u0443\u0442 \u0445\u043e\u0447\u0435\u0442\u0441\u044f \u043e\u0442\u043c\u0435\u0442\u0438\u0442\u044c, \u0447\u0442\u043e \u0446\u0432\u0435\u0442 \u0437\u0430\u0434\u043d\u0435\u0439 \u043f\u0430\u043d\u0435\u043b\u0438 \u043d\u0435 \u044f\u0440\u043a\u0438\u0439, \u0430 \u0441\u043a\u043e\u0440\u0435\u0435 \u0431\u043b\u0430\u0433\u043e\u0440\u043e\u0434\u043d\u044b\u0439, \u044f \u0431\u044b \u0434\u0430\u0436\u0435 \u043d\u0430\u0437\u0432\u0430\u043b\u0430 \u0435\u0433\u043e \u043d\u0435 \u0437\u043e\u043b\u043e\u0442\u0438\u0441\u0442\u044b\u043c, \u0430 \u0446\u0432\u0435\u0442\u043e\u043c \"\u0448\u0430\u043c\u043f\u0430\u043d\u044c\")) \u041d\u0430 \u0442\u044b\u043b\u044c\u043d\u043e\u0439 \u0441\u0442\u043e\u0440\u043e\u043d\u0435 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d\u0430 \u0438\u043c\u0435\u0435\u0442\u0441\u044f \u0441\u043a\u0430\u043d\u0435\u0440 \u043e\u0442\u043f\u0435\u0447\u0430\u0442\u043a\u0430 \u043f\u0430\u043b\u044c\u0446\u0430. \u0418 \u0432\u043e\u0442 \u043e \u043d\u0435\u043c \u0445\u043e\u0447\u0435\u0442\u0441\u044f \u043e\u0442\u0434\u0435\u043b\u044c\u043d\u043e \u043d\u0435\u043c\u043d\u043e\u0433\u043e \u0441\u043a\u0430\u0437\u0430\u0442\u044c.. \u0412 \u0432\u0435\u0440\u0445\u043d\u0435\u043c \u0443\u0433\u043b\u0443 \u0440\u0430\u0441\u043f\u043e\u043b\u0430\u0433\u0430\u0435\u0442\u0441\u044f \u043a\u0430\u043c\u0435\u0440\u0430 \u0441\u043e \u0432\u0441\u043f\u044b\u0448\u043a\u043e\u0439. \u0412\u043d\u0438\u0437\u0443 \u0437\u043d\u0430\u0447\u043e\u043a Mi. \u0414\u0438\u0437\u0430\u0439\u043d \u043f\u0440\u043e\u0441\u0442\u043e\u0439, \u0438 \u0432 \u0442\u043e \u0436\u0435 \u0432\u0440\u0435\u043c\u044f \u044d\u043b\u0435\u0433\u0430\u043d\u0442\u043d\u044b\u0439, \u043d\u0438\u0447\u0435\u043c \u043b\u0438\u0448\u043d\u0438\u043c \u043d\u0435 \u043f\u0435\u0440\u0435\u0433\u0440\u0443\u0436\u0435\u043d, \u0438 \u043f\u0440\u0438 \u044d\u0442\u043e\u043c \u0441\u043c\u043e\u0442\u0440\u0438\u0442\u0441\u044f \u043e\u0447\u0435\u043d\u044c \u043a\u0440\u0430\u0441\u0438\u0432\u043e. \u0413\u0430\u0431\u0430\u0440\u0438\u0442\u044b \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0430: 88.3x173.1x7.5 \u043c\u043c. \u041a\u0430\u043a \u0432\u0438\u0434\u0438\u043c - \u043e\u043d \u0431\u043e\u043b\u044c\u0448\u043e\u0439 \u0438 \u0442\u043e\u043d\u043a\u0438\u0439. \u041f\u0440\u0438\u0447\u0435\u043c \u0440\u0435\u0430\u043b\u044c\u043d\u043e \u0442\u043e\u043d\u043a\u0438\u0439.. \u041d\u0430 \u043f\u0440\u043e\u0442\u0438\u0432\u043e\u043f\u043e\u043b\u043e\u0436\u043d\u043e\u0439 \u0436\u0435 \u0433\u0440\u0430\u043d\u0438 \u043d\u0430\u0445\u043e\u0434\u0438\u0442\u0441\u044f \u043b\u043e\u0442\u043e\u043a \u0434\u043b\u044f \u0441\u0438\u043c \u043a\u0430\u0440\u0442. \u041e\u043d \u0433\u0438\u0431\u0440\u0438\u0434\u043d\u044b\u0439. \u041c\u043e\u0436\u043d\u043e \u0438\u0441\u043f\u043e\u043b\u044c\u0437\u043e\u0432\u0430\u0442\u044c \u0438\u043b\u0438 2 \u0441\u0438\u043c \u043a\u0430\u0440\u0442\u044b (\u043c\u0438\u043a\u0440\u043e \u0438 \u043d\u0430\u043d\u043e), \u0438\u043b\u0438 \u043e\u0434\u043d\u0443 \u0441\u0438\u043c \u043a\u0430\u0440\u0442\u0443 (\u043c\u0438\u043a\u0440\u043e) \u0438 \u043a\u0430\u0440\u0442\u0443 \u043f\u0430\u043c\u044f\u0442\u0438. \u0421\u0432\u0435\u0440\u0445\u0443 \u0440\u0430\u0441\u043f\u043e\u043b\u0430\u0433\u0430\u0435\u0442\u0441\u044f \u0440\u0430\u0437\u044a\u0435\u043c \u0434\u043b\u044f \u0433\u0430\u0440\u043d\u0438\u0442\u0443\u0440\u044b, \u0441\u043d\u0438\u0437\u0443 \u0434\u0438\u043d\u0430\u043c\u0438\u043a, \u043c\u0438\u0