35\u0439\u0441\u0442\u0432\u043e\u0432\u0430\u0442\u044c \u0432 \u043d\u0430\u0448\u0435\u0439 \u0441\u0442\u0440\u0430\u043d\u0435. \u0415\u0441\u0442\u044c \u0435\u0449\u0435 \u0433\u043e\u0434\u043e\u0432\u0430\u044f \u0433\u0430\u0440\u0430\u043d\u0442\u0438\u044f \u043e\u0431\u043c\u0435\u043d\u0430, \u043d\u043e \u044f \u043d\u0435 \u0440\u0435\u0448\u0438\u043b\u0430\u0441\u044c \u0435\u0435 \u0432\u0437\u044f\u0442\u044c. \u0413\u0430\u0440\u0430\u043d\u0442\u0438\u0438 \u0434\u0435\u0439\u0441\u0442\u0432\u0443\u044e\u0442 \u0432\u0435\u0437\u0434\u0435, \u0433\u0434\u0435 \u0435\u0441\u0442\u044c \u043f\u0443\u043d\u043a\u0442\u044b \u0441\u0435\u0440\u0432\u0438\u0441\u0430 \"\u0421\u0432\u044f\u0437\u043d\u043e\u0433\u043e\". , \u043f\u043e\u043b\u0443\u0447\u0438\u043b\u0430   \u0412 \u0440\u0435\u0437\u0443\u043b\u044c\u0442\u0430\u0442\u0435 \u043e\u043d \u043f\u043e\u043b\u0443\u0447\u0438\u043b\u0441\u044f \u0435\u0449\u0435 \u0434\u0435\u0448\u0435\u0432\u043b\u0435. \u00a0  \u041f\u0440\u0438\u0448\u043b\u0430 \u043f\u043e\u0441\u044b\u043b\u043a\u0430 \u0432 \u043e\u0431\u044b\u0447\u043d\u043e\u043c \u0441\u0435\u0440\u043e\u043c \u043f\u043e\u043b\u0438\u044d\u0442\u0438\u043b\u0435\u043d\u043e\u0432\u043e\u043c \u043f\u043e\u0447\u0442\u043e\u0432\u043e\u043c \u043c\u0435\u0448\u043a\u0435. \u041a\u043e\u0440\u043e\u0431\u043a\u0430 \u0431\u044b\u043b\u0430 \u043d\u0430\u0434\u0435\u0436\u043d\u043e \u0437\u0430\u0432\u0435\u0440\u043d\u0443\u0442\u0430 \u0432 \u043f\u043b\u043e\u0442\u043d\u044b\u0439 \u043f\u043e\u0440\u043e\u043b\u043e\u043d. \u00a0 \u00a0 \u00a0 \u00a0 \u00a0 \u00a0 \u041a\u0430\u043a \u0438 \u043c\u043d\u043e\u0433\u0438\u0435 \u0441\u0435\u0439\u0447\u0430\u0441 \u043f\u0440\u043e\u0438\u0437\u0432\u043e\u0434\u0438\u0442\u0435\u043b\u0438 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d\u043e\u0432, Xiaomi \u0438\u0437 \u044d\u0442\u043e\u0439 \u043c\u043e\u0434\u0435\u043b\u0438 \u043f\u043e\u0441\u0442\u0430\u0440\u0430\u043b\u0441\u044f \u0441\u0434\u0435\u043b\u0430\u0442\u044c \u0430\u043d\u0430\u043b\u043e\u0433 \u0410\u0439\u0444\u043e\u043d\u0443. \u0421\u0432\u0438\u0434\u0435\u0442\u0435\u043b\u044c\u0441\u0442\u0432\u043e \u044d\u0442\u043e\u043c\u0443, \u043d\u0430\u043f\u0440\u0438\u043c\u0435\u0440, \u043f\u0440\u0438\u0431\u043b\u0438\u0436\u0435\u043d\u043d\u044b\u0439 \u0434\u0438\u0437\u0430\u0439\u043d, \u00a0 \u00a0 . \u00a0 \u00a0 \u0427\u0435\u043c \u0436\u0435 \u043e\u043d \u043e\u0442\u043b\u0438\u0447\u0430\u0435\u0442\u0441\u044f \u0438 \u0442\u0430\u043a \u0445\u043e\u0440\u043e\u0448? \u0410 \u0442\u0435\u043c, \u0447\u0442\u043e \u0441 \u043d\u0438\u043c \u043d\u0430\u043c \u043e\u0431\u0435\u0449\u0430\u044e\u0442  . \u0422\u0430\u043a\u0436\u0435,     \u0432\u0441\u0442\u0430\u0432\u043b\u044f\u0442\u044c \u043c\u043e\u0436\u043d\u043e \u043b\u044e\u0431\u043e\u0439 \u0441\u0442\u043e\u0440\u043e\u043d\u043e\u0439.  \u00a0 \u00a0 \u00a0 \u00a0 \u0418 \u043a\u0430\u043a \u0431\u0443\u0434\u0442\u043e \u0434\u0430\u0436\u0435 \u0442\u043e\u043d\u044c\u0448\u0435, \u0447\u0435\u043c \u0432 \u043e\u043f\u0438\u0441\u0430\u043d\u0438\u0438. \u042f \u0431\u043e\u044f\u043b\u0430\u0441\u044c, \u0447\u0442\u043e \u043c\u043d\u0435 \u043e\u043d \u0431\u0443\u0434\u0435\u0442 \u043a\u0430\u0437\u0430\u0442\u044c\u0441\u044f \u043e\u0447\u0435\u043d\u044c \u0431\u043e\u043b\u044c\u0448\u0438\u043c. \u041d\u043e \u0442\u0435\u043b\u0435\u0444\u043e\u043d \u043e\u043a\u0430\u0437\u0430\u043b\u0441\u044f \u043a\u043e\u043c\u043f\u0430\u043a\u0442\u043d\u044b\u043c, \u0438 \u044f \u043a \u043d\u0435\u043c\u0443 \u0431\u044b\u0441\u0442\u0440\u043e \u043f\u0440\u0438\u0432\u044b\u043a\u043b\u0430. \u042d\u0442\u0438 5 \u0434\u044e\u0439\u043c\u043e\u0432 \u0432\u044b\u0433\u043b\u044f\u0434\u044f\u0442 \u043b\u0435\u0433\u0447\u0435 3,5\u201d \u043c\u043e\u0435\u0433\u043e \u00ab\u0441\u0442\u0430\u0440\u0438\u0447\u043a\u0430\u00bb. \u00a0 \u00a0 \u00a0  \u0418\u043c\u0435\u0435\u0442\u0441\u044f   (\u043f\u043b\u0435\u043d\u043a\u0430, \u043e\u0442\u0442\u0430\u043b\u043a\u0438\u0432\u0430\u044e\u0449\u0430\u044f \u0436\u0438\u0440\u044b \u043e\u0442 \u0441\u0435\u043d\u0441\u043e\u0440\u043d\u043e\u0433\u043e \u044d\u043a\u0440\u0430\u043d\u0430). \u041e\u0442\u043f\u0435\u0447\u0430\u0442\u043a\u043e\u0432 \u043f\u0440\u0430\u043a\u0442\u0438\u0447\u0435\u0441\u043a\u0438 \u043d\u0435 \u043e\u0441\u0442\u0430\u0435\u0442\u0441\u044f, \u043a\u043e\u0442\u043e\u0440\u044b\u0435 \u043c\u043e\u0436\u043d\u043e \u0434\u0430\u0436\u0435 \u043f\u0430\u043b\u044c\u0446\u0430\u043c\u0438 \u0438 \u0441\u0442\u0435\u0440\u0435\u0442\u044c.  (\u0431\u044b\u043b\u043e \u0432\u0440\u0435\u043c\u044f, \u043a\u043e\u0433\u0434\u0430 \u043b\u044e\u0434\u0438 \u043e\u0442\u043a\u0430\u0437\u044b\u0432\u0430\u043b\u0438\u0441\u044c \u043f\u043e\u043a\u0443\u043f\u0430\u0442\u044c \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u044b \u0441 \u0435\u043c\u043a\u043e\u0441\u0442\u043d\u044b\u043c\u0438 \u044d\u043a\u0440\u0430\u043d\u0430\u043c\u0438 \u0438\u0437-\u0437\u0430 \u0442\u043e\u0433\u043e, \u0447\u0442\u043e \u043e\u043d\u0438 \u0432\u0441\u0435 \u0432\u0440\u0435\u043c\u044f \u0431\u044b\u043b\u0438 \u0432 \u0436\u0438\u0440\u043d\u044b\u0445 \u043e\u0442\u043f\u0435\u0447\u0430\u0442\u043a\u0430\u0445 \u043f\u0430\u043b\u044c\u0446\u0435\u0432). \u041c\u043e\u0435\u0433\u043e \"\u0441\u0442\u0430\u0440\u0438\u0447\u043a\u0430\" \u0432\u0441\u0435 \u0432\u0440\u0435\u043c\u044f \u043d\u0430\u0434\u043e \u0431\u044b\u043b\u043e \u043f\u0440\u043e\u0442\u0438\u0440\u0430\u0442\u044c. \u00a0  \u042f \u0442\u0435\u043f\u0435\u0440\u044c \u0441\u043c\u043e\u0442\u0440\u044e \u043d\u0430 \u0441\u0432\u043e\u0439 \u0441\u0442\u0430\u0440\u0435\u043d\u044c\u043a\u0438\u0439 \u0441\u0430\u043c\u0441\u0443\u043d\u0433 \u0438 \u0443\u0434\u0438\u0432\u043b\u044f\u044e\u0441\u044c: \u043a\u0430\u043a \u044f \u0432\u043e\u043e\u0431\u0449\u0435 \u043d\u0430 \u043d\u0435\u043c \u0447\u0442\u043e-\u0442\u043e \u0432\u0438\u0434\u0435\u043b\u0430. \u00a0 \u00a0 \u041e\u0441\u043d\u043e\u0432\u043d\u0430\u044f \u043a\u0430\u043c\u0435\u0440\u0430 \u043e\u0431\u043b\u0430\u0434\u0430\u0435\u0442 13-\u043c\u0435\u0433\u0430\u043f\u0438\u043a\u0441\u0435\u043b\u044c\u043d\u044b\u043c \u0441\u0432\u0435\u0442\u043e\u0447\u0443\u0432\u0441\u0442\u0432\u0438\u0442\u0435\u043b\u044c\u043d\u044b\u043c \u044d\u043b\u0435\u043c\u0435\u043d\u0442\u043e\u043c. \u041e\u043d\u0430 \u0434\u0435\u043c\u043e\u043d\u0441\u0442\u0440\u0438\u0440\u0443\u0435\u0442 \u043f\u0440\u0435\u0432\u043e\u0441\u0445\u043e\u0434\u043d\u044b\u0439 \u0443\u0440\u043e\u0432\u0435\u043d\u044c \u0444\u043e\u043a\u0443\u0441\u0438\u0440\u043e\u0432\u043a\u0438, \u0431\u043b\u0430\u0433\u043e\u0434\u0430\u0440\u044f \u0432\u043d\u0435\u0434\u0440\u0435\u043d\u0438\u044e \u0444\u0430\u0437\u043e\u0432\u043e\u0439 \u0444\u043e\u043a\u0443\u0441\u0438\u0440\u043e\u0432\u043a\u0438, \u043a\u043e\u0442\u043e\u0440\u0430\u044f \u043e\u0431\u044b\u0447\u043d\u043e \u043f\u0440\u0438\u043c\u0435\u043d\u044f\u0435\u0442\u0441\u044f \u0432 \u0437\u0435\u0440\u043a\u0430\u043b\u044c\u043d\u044b\u0445 \u0444\u043e\u0442\u043e\u0430\u043f\u043f\u0430\u0440\u0430\u0442\u0430\u0445. \u0422\u0435\u043f\u0435\u0440\u044c \u0430\u0432\u0442\u043e\u0444\u043e\u043a\u0443\u0441\u043e\u0440\u0438\u0432\u043a\u0430 \u0437\u0430\u043d\u0438\u043c\u0430\u0435\u0442 \u0432\u0441\u0435\u0433\u043e 0.1 \u0441\u0435\u043a\u0443\u043d\u0434\u044b. \u0415\u0441\u0442\u044c \u0432\u043e\u0437\u043c\u043e\u0436\u043d\u043e\u0441\u0442\u044c \u0442\u0430\u043a\u0436\u0435 \u0434\u0435\u043b\u0430\u0442\u044c \u0437\u0430\u043c\u0435\u0447\u0430\u0442\u0435\u043b\u044c\u043d\u044b\u0435 \u00ab\u0441\u0435\u043b\u0444\u0438\u00bb \u0441 \u043f\u043e\u043c\u043e\u0449\u044c\u044e 5-\u043c\u0435\u0433\u0430\u043f\u0438\u043a\u0441\u0435\u043b\u044c\u043d\u043e\u0439 \u0444\u0440\u043e\u043d\u0442\u0430\u043b\u044c\u043d\u043e\u0439 \u043a\u0430\u043c\u0435\u0440\u044b. \u00a0 \u00a0 \u00a0 \u041f\u043e \u043c\u043d\u0435, \u0434\u0430\u0436\u0435 \u0441\u043b\u0438\u0448\u043a\u043e\u043c. \u041d\u0430\u043f\u0440\u0438\u043c\u0435\u0440, \u043f\u0440\u043e\u0433\u0440\u0430\u043c\u043c\u0430 \u043a\u0430\u043c\u0435\u0440\u044b \u0432\u0440\u0435\u043c\u0435\u043d\u0430\u043c\u0438 \u0437\u0430\u0434\u0438\u0440\u0430\u0435\u0442 \u043a\u0440\u0430\u0441\u043d\u044b\u0439 \u0438 \u0432 \u0440\u0435\u0437\u0443\u043b\u044c\u0442\u0430\u0442\u0435 \u043e\u043d \u043d\u0435 \u0442\u043e\u043b\u044c\u043a\u043e \u043e\u0447\u0435\u043d\u044c \u0441\u043e\u0447\u043d\u044b\u0439 \u043f\u043e\u043b\u0443\u0447\u0430\u0435\u0442\u0441\u044f, \u043d\u043e \u0442\u0435\u0440\u044f\u044e\u0442\u0441\u044f \u0434\u0435\u0442\u0430\u043b\u0438. \u0412\u043e\u0442 \u0444\u043e\u0442\u043e \u0434\u043b\u044f \u0441\u0440\u0430\u0432\u043d\u0435\u043d\u0438\u044f (\u043d\u0430 \u043f\u0440\u0430\u0432\u043e\u043c \u0444\u043e\u0442\u043e \u0437\u0430 \u0441\u0447\u0435\u0442 \u0442\u0430\u043a\u043e\u0433\u043e \u044f\u0440\u043a\u043e\u0433\u043e \u043a\u0440\u0430\u0441\u043d\u043e\u0433\u043e \u0441\u0442\u0440\u0430\u0434\u0430\u0435\u0442 \u0447\u0435\u0442\u043a\u043e\u0441\u0442\u044c \u0446\u0432\u0435\u0442\u043a\u0430. \u041d\u043e \u0441\u0438\u043c\u043f\u0430\u0442\u0438\u0447\u043d\u043e   ). \u00a0 \u00a0 \u041c\u043e\u0436\u0435\u0442 \u044f \u043f\u0440\u043e\u0441\u0442\u043e \u0435\u0449\u0435 \u043f\u043b\u043e\u0442\u043d\u043e \u0441 \u043d\u0430\u0441\u0442\u0440\u043e\u0439\u043a\u0430\u043c\u0438 \u043d\u0435 \u0437\u0430\u043d\u0438\u043c\u0430\u043b\u0430\u0441\u044c. \u0422\u0430\u043c \u043c\u043e\u0436\u043d\u043e \u0431\u0430\u043b\u0430\u043d\u0441 \u0431\u0435\u043b\u043e\u0433\u043e \u0432\u044b\u0441\u0442\u0430\u0432\u0438\u0442\u044c, \u043d\u0430\u043f\u0440\u0438\u043c\u0435\u0440. \u0418\u0437\u043c\u0435\u043d\u0438\u0442\u044c \u0444\u043e\u043a\u0443\u0441 \u0438 \u0442.\u0434. \u0412 \u043d\u0430\u0441\u0442\u0440\u043e\u0439\u043a\u0430\u0445 \u043a\u0430\u043c\u0435\u0440\u044b \u0435\u0441\u0442\u044c \u0440\u0430\u0437\u043b\u0438\u0447\u043d\u044b\u0435 \u0440\u0435\u0436\u0438\u043c\u044b \u0441\u044a\u0435\u043c\u043a\u0438. \u0415\u0441\u0442\u044c \u0432\u043e\u0437\u043c\u043e\u0436\u043d\u043e\u0441\u0442\u044c \u0441\u0440\u0430\u0437\u0443 \u0436\u0435 \u043d\u0430 \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0435 \u043e\u0431\u0440\u0430\u0431\u043e\u0442\u0430\u0442\u044c \u0444\u043e\u0442\u043e\u0433\u0440\u0430\u0444\u0438\u044e. \u0412\u043e\u0442 \u043f\u0440\u043e\u0441\u0442\u043e \u043f\u0440\u0438\u043c\u0435\u0440, \u043a\u0430\u043a \u0441 \u043f\u043e\u043c\u043e\u0449\u044c\u044e \u044d\u0442\u043e\u0433\u043e \u0442\u0435\u043b\u0435\u0444\u043e\u043d\u0430 \u043c\u043e\u0436\u0435\u0442 \u0441\u0438\u043c\u043f\u0430\u0442\u0438\u0447\u043d\u043e \u043f\u043e\u043b\u0443\u0447\u0438\u0442\u0441\u044f \u0446\u0432\u0435\u0442\u043e\u0447\u0435\u043a \u0438\u0437... \u0441\u0442\u0440\u0430\u043d\u043d\u043e\u0433\u043e \u0440\u0430\u0441\u0442\u0435\u043d\u0438\u044f  \u00a0 \u00a0 \u00a0 \u00a0 \u0412\u044b\u0441\u043e\u043a\u0430\u044f \u043f\u0440\u043e\u0438\u0437\u0432\u043e\u0434\u0438\u0442\u0435\u043b\u044c\u043d\u043e\u0441\u0442\u044c \u2013 \u043d\u0435 \u0435\u0434\u0438\u043d\u0441\u0442\u0432\u0435\u043d\u043d\u0430\u044f \u043f\u0440\u0438\u0447\u0438\u043d\u0430, \u043f\u043e \u043a\u043e\u0442\u043e\u0440\u043e\u0439 \u043f\u0440\u0435\u0434\u043f\u043e\u0447\u0438\u0442\u0430\u044e\u0442 \u0441\u043c\u0430\u0440\u0442\u0444\u043e\u043d\u044b Xiaomi. \u0423\u0436\u0435 \u0431\u043e\u043b\u0435\u0435 100 \u043c\u0438\u043b\u043b\u0438\u043e\u043d\u043e\u0432 \u0447\u0435\u043b\u043e\u0432\u0435\u043a \u043e\u0446\u0435\u043d\u0438\u043b\u0438 \u043f\u043e\u0442\u0440\u044f\u0441\u0430\u044e\u0449\u0443\u044e \u043e\u043f\u0435\u0440\u04